INSERT INTO PITANJE (idPitanje, korisnik_idKorisnik_postavio,kategorijaPitanja_idKategorija,naslovPitanja,tekstPitanja,postavljenoAnonimno,moguSviDaOdgovore) VALUES
(1, 1, 3, 'Problemi sa paničnim napadima', 'Jako dugo imam probleme sa paničnim napadima. Osećam stezanje u grlu sto je postalo baš problem sada, uzimao sam lekove kao terapiju sto mi je psihijatar prepisao i sam sam prestao da ih uzimam. Smatrao sam da mi više ne trebaju. Prošle nedelje sam imao učestale napade. Imam fizicke probleme kao stezanje u grlu i bol srca. Kako da pomognem sam sebi? Da li je bolje posavetovati se sa stručnim licem ili da sam nastavim sa prethodnom terapijom?',0,1),
(2, 2, 1, 'Anksioznost', 'Anksioznost je problem koji me muči već godinama. Pokušavala sam na razne načine da ga rešim, išla sam kod psihologa i psihijatra, ali nije bilo većih poboljšanja. Možda zato što nisam bila spremna da otvoreno govorim sa njima. Strah me je da podelim priču sa bliskim osobama, jer ne znam da li će me razumeti. Niko ne razume koliko truda ulažem u svakodnevne razgovore, ma koliko oni bili kratki. Plašim se raznih stvari i imam običaj da ruke perem više puta detaljno, da mi cela kuća bude sređena. Da li je ovo stvarno anksioznost ili može biti nešto drugo? Kome da se obratim?',1,1),
(3, 3, 7, 'Ne verujem u sebe', 'U poslednje vreme ne verujem sopstvenom mišljenju, preterano razmisljam o svemu i strah me je da prihvatim izazove. Konstantno sam zabrinut da neću postići sve što sam želeo. Zbog toga sam često previše strog prema sebi, a popustljiv prema drugima. Voleo bih to da promenim, pa Vas molim za savet.',1,0),
(4, 4, 8, 'Da li imam OKP?', 'Sve je počelo u januaru ove godine, anksioznost, depresija itd. Imao sam maltene strah o budućnosti, šta će se desiti, kasnije je počela škola imao sam jako slabu koncetraciju, nisam mogao da pratim profesora šta govori, a odličan sam đak te mi je to išlo na muku. Otišao sam kod pishologa, objasnio joj sve ona me poslala kod psihijatra, ali ništa nije pomoglo. Da li imam OKP?',0,0),
(5, 5, 9, 'Mislim da bolujem od depersonalizacije', 'Zdravo! Jako bi mi značio savet povodom simptoma koje trenutno imam: osećam kao da sam izvan svog tela, ponekad kao da gledam sebe odozgo, osećam da sam odvojen od sebe, kao da, zapravo, nemam sebe,blokada u umu ili telu, kao da su mi čula isključena, kao da ne mogu da kontrolišem šta radim ili šta izgovaram. Da li je moguće da imam depersonalizaciju i kako da je rešim? Hvala na odgovoru!',0,1);

INSERT INTO ODGOVOR (idOdgovor,pitanje_idPitanje,korisnik_idKorisnik_odgovorio,tekstOdgovora,odgovorenoAnonimno) VALUES
(1,1,2,'Zdravo! I sam sam se borio sa istim problemom, ali sam ga uspešno rešio uz pomoć psihologa. Preporučujem ti da ne uzimaš sam na svoju ruku nijednu vrstu leka, jer posledice mogu biti opasne. Proslo je 2 godine od kada sam imao fizičke probleme odsnosno stezanje u grlo i znam kako ti je. Srećno!',1),
(2,1,6,'Poštovani, U svojoj dugogodnišnjoj praksi sam se mnogo puta susretao sa pacijentima koji su imali isti ili sličan problem kao što je Vaš. Nikako nemojte da uzimate lekove na svoju ruku, jer to može dovesti do naglih promena raspoloženja i samog statusa organizma, pa kad izazvati da se osećate gore nego trenutno. Važno je praviti male korake ka uspehu. Najbolje bi bilo da kroz sve situacije koje predstavljaju okidač na početku prolazite uz pomoć bliske osobe uz koju se osecaće sigurno i zaštićeno. Za više informacija stojim Vam na raspolaganju.',0),
(3,2,1,'To nije problem koji se rešava preko noći, samo strpljivo. Ja bih ti preporučio da potražiš stručnu pomoć',1),
(4,3,6,'Poštovani, Nemojte previše da budete zabrinuti. Način na koji razmišljate i kako se osećate nije ništa neobičan. Svi mi smo imali takve faze u životu, ali je bitno uočiti problem na vreme i raditi na njemu. Pozdrav!',0),
(5,3,7,'Drago mi je da ste postavili ovo pitanje, zato što je to jedan od najčešćih problema današnjice. Voleo bih da Vam pomognem, ali je to nemoguće putem kratkog odgovora. Preporučio bih Vam da se prijavite na neku od seansi koje se bave baš temom koja i Vas muči. Za Više informacija, slobodno me kontaktirajte.',0);

